TriggerOraclev10g.body:
BEGIN
  IF (:new.data_postare < TO_DATE('01.01.2026', 'DD.MM.YYYY'))
  THEN
   RAISE_APPLICATION_ERROR( -20001,
    'Data invalida: ' || TO_CHAR(:new.data_postare, 'DD.MM.YYYY HH24:MI:SS') ||
    'trebuie sa fie dupa 01.01.2026');
  END IF;

  IF (:new.data_postare > SYSDATE)
  THEN
   RAISE_APPLICATION_ERROR( -20002,
    'Data invalida: ' || TO_CHAR(:new.data_postare, 'DD.MM.YYYY HH24:MI:SS') ||
    'nu poate fi in viitor');
   END IF;
END;
